import React, { useContext, useState } from 'react'
import noteContext from "./notes/noteContext"

const Home = () => {
    const context = useContext(noteContext);
    const { addNote } = context;

    const [note, setNote] = useState({ ename: "", eid: "", department: "" })

    const handleClick = (e) => {
        e.preventDefault();
       // addNote(note.ename, note.eid, note.department);
        setNote({ ename: "", eid: "", department: "" })
    }

    const onChange = (e) => {
        setNote({ ...note, [e.target.name]: e.target.value })
    }   




    return (
        <div className='center' style={{ backgroundColor: 'rgba(255, 255, 255, 0.5)', padding: '20px', borderRadius: '5px' }}>


            {/* input field */}
            <div className="mb-3">
                <label for="ename" className="form-label">Employee Name</label>
                <input type="text" className="form-control" id="ename" placeholder="Please enter your name" value={note.ename} onChange={onChange} minLength={3} required />
            </div>
            <div className="mb-3">
                <label for="eid" className="form-label">Employee Id</label>
                <input type="number" className="form-control" id="eid" placeholder="Please enter your id" value={note.eid} onChange={onChange} required />
            </div>
            <div className="mb-3">
                <label for="department" className="form-label">Department</label>
                <input type="text" className="form-control" id="department" placeholder="Please enter your department" value={note.department} onChange={onChange} />
            </div>




            {/* button */}
            <div className="d-grid gap-2">
                <button className="btn btn-primary" type="submit" onClick={handleClick}>Submit</button>

            </div>
        </div>
    )
}

export default Home
