import React, {useContext, useState} from 'react'
import noteContext from "./notes/noteContext"

const Home = () => {
  const context = useContext(noteContext);
    const {addNote} = context;

    const [note, setNote] = useState({title: "", description: "", tag: ""})

    const handleClick = (e)=>{
        e.preventDefault();
        addNote(note.ename, note.eid, note.department);
        setNote({ename: "", eid: "", department: ""})
    }

    const onChange = (e)=>{
        setNote({...note, [e.target.name]: e.target.value})
    }




    return (
        <div className='center'style={{ backgroundColor: 'rgba(255, 255, 255, 0.5)', padding: '20px',borderRadius: '5px' }}>


            {/* input field */}
            <div className="mb-3">
                <label for="ename" className="form-label">Employee Name</label>
                <input   type="text" className="form-control" id="ename"  placeholder="Please enter your name" />
            </div>
            <div className="mb-3">
                <label for="eid" className="form-label">Employee Id</label>
                <input type="number" className="form-control" id="eid" placeholder="Please enter your id" />
            </div>
            <div className="mb-3">
                <label for="department" className="form-label">Department</label>
                <input type="text" className="form-control" id="department" placeholder="Please enter your department" />
            </div>


            {/* radio button */}
            <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="inlineRadioOptions" id="onset" value="option1" />
                <label className="form-check-label" for="onset">onset</label>
            </div>
            <div className="form-check form-check-inline">
                <input className="form-check-input" type="radio" name="inlineRadioOptions" id="offset" value="option2" />
                <label className="form-check-label" for="offset">offset</label>
            </div>

             

            {/* dropdownlist */}
            <div className="input-group mb-3">
                  <select style={{ outline: 'none', border: '1px solid #ccc', padding: '5px' , borderRadius: '5px', overflow: 'hidden' }}>
        <option value="option1">Tea</option>
        <option value="option2">Coffee</option>
        <option value="option3">Others</option>
      </select>
                <input type="number" class="form-control" aria-label="Text input with dropdown button " placeholder="Quantity" />
            </div>


            {/* button */}
<div className="d-grid gap-2">
  <button className="btn btn-primary" type="button">Submit</button>

</div>     
</div>
    )
}

export default Home
