import React, { useState } from 'react'
const Home = () => {


  const [showMessage, setShowMessage] = useState(false);

  const [note, setNote] = useState({ ename: "", eid: "", department: "" });
  const handleClick = (e) => {
    e.preventDefault();
    console.log(note);
    sendDataToBackend(note);


    setShowMessage(true); // Show the success message
    setTimeout(() => {
      setShowMessage(false); // Hide the message after 3 seconds
    }, 3000);

  }
  const onChange = (e) => {
    setNote({ ...note, [e.target.name]: e.target.value })
  }

  const sendDataToBackend = async (dataArray) => {
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {

        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataArray)


      });

      if (response.ok) {
        const result = await response.json();
        console.log('Data sent successfully:', result);
      } else {
        console.error('Failed to send data:', response.statusText);
        // console.log(JSON.stringify( dataArray ))

      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className='center' style={{ backgroundColor: 'rgba(255, 255, 255, 0.5)', padding: '20px', borderRadius: '5px' }}>


      {/* input field */}
      <div className="mb-3">
        <label htmlFor="ename" className="form-label">Employee Name</label>
        <input type="text" className="form-control" name="ename" id="ename" placeholder="Please enter your name" value={note.ename} onChange={onChange} required />
      </div>
      <div className="mb-3">
        <label htmlFor="eid" className="form-label">Employee Id</label>
        <input type="number" className="form-control" name="eid" id="eid" placeholder="Please enter your id" value={note.eid} onChange={onChange} required />
      </div>
      <div className="mb-3">
        <label htmlFor="department" className="form-label">Department</label>
        <input type="text" className="form-control" name="department" id="department" placeholder="Please enter your department" value={note.department} onChange={onChange} />
      </div>


      {/* dropdown */}
      <div className="mb-3">
  <label htmlFor="snacks" className="form-label">Snacks</label>
  <select
    className="form-control"
    name="snacks"
    id="snacks"
    value={note.department}
    onChange={onChange}
    required
  >
    <option value="">Select your meal</option>
    <option value="tea">Tea</option>
    <option value="coffee">Coffee</option>
    <option value="others">Others</option>
    {/* Add more options as needed */}
  </select>
</div>







      {/* button */}
      <div className="d-grid gap-2">
        <button disabled={note.ename.length < 3 || note.eid.length < 5 || note.department.length < 2} className="btn btn-primary" type="submit" onClick={handleClick}>Submit</button>


        {showMessage && (
          <div style={{ marginTop: "10px", color: "green", fontWeight: "bold" }}>
            Success!
          </div>
        )}
      </div>

    </div>

  )
}

export default Home
